import React, { useEffect, useReducer } from 'react';
import styled from 'styled-components';
import axios from "axios";

const search = "홍대맛집";
const BASE_URL = "http://whrbdnjs33.asuscomm.com:5432";

function reducer (state, action) {
    switch (action.type) {
        case 'LOADING' :
            return {
                loading: true,
                data: null,
                error: null
            };
        case 'SUCCESS' :
            return {
                loading: false,
                data: action.data,
                error: null
            };
        case 'ERROR' :
            return {
                loading: false,
                data: null,
                error: action.error
            };
        default:
            throw new Error(`Unhandled action type: ${action.type}`);
    }
}

function ListThree() {

    const [state, dispatch] = useReducer(reducer, {
        loading: false,
        data: null,
        error: null
    });

    const fetchPosts = async () => {
        dispatch({type: 'LOADING'});
        try {
            const response = await axios.get(
                `http://whrbdnjs33.asuscomm.com:5432/matzip/?search=${search}`
            );
            dispatch({ type: 'SUCCES', data:response.data});
        } catch (e) {
            dispatch({type: 'ERROR', error: e});
        }
    };

    useEffect(() => {
        fetchPosts();
    }, []);

    const { loading, data, error } = state;

    if (loading) return <div>로딩중..</div>;
    if (error) return <div>에러가 발생했습니다</div>;
    if (!data) return null;
    return (
        <>
            {data.map((post) => post.allowed ?
                <Image>
                    <Item src={post.url} />
                </Image>
                : null
            )}
        </>
    )
}

const Item = styled.img`
    width: 256px;
    height: 256px;
    object-fit: cover;
`;

const Image = styled.div`
    width: 256px;
    height: 256px;
    padding: 3px;
`;

export default ListThree;
